                    w = 0,
                    outerMargin = EDGES_BLEED,
                    extrusion = EDGE_BLEED,
                    rt = null,
                    sx = this.scaleX,
                    sy = this.scaleY,
                    th = this.tileHeight,
                    tw = this.tileWidth,
                    tsh = this.tilesHeight,
